---@diagnostic disable: undefined-global
project "Assimp"
    kind "StaticLib"
    language "C++"
    staticruntime "on"

    local output_dir = "%{cfg.buildcfg}-%{cfg.system}-%{cfg.architecture}"

    targetdir ("../../bin/" .. output_dir .. "/%{prj.name}")
    objdir ("../../obj/" .. output_dir .. "/%{prj.name}")

    includedirs {
        "assimp/include/",
        "assimp/code/",
    }

    defines {
